import styled from "styled-components";
import PropsBox1 from "./Components/Props/PropsBox1";
import { useDispatch } from "react-redux";
import { useState } from "react";
import { plus, minus } from "./Components/modules/counter";
import ReduxBox1 from "./Components/Redux/ReduxBox1";

const BackDiv = styled.div`
  display:flex;
  align-items:center;
  justify-content:center;
  height:100vh;
`

const StartPropsBox = styled.div`
    display: flex;
    align-items:center;
    justify-content:center;
    width:40%;
    height: 90%;
    border:solid 2px black;
    margin:5px;
`

const StartReduxBox = styled.div`
    display: flex;
    align-items:center;
    justify-content:center;
    width:40%;
    height: 90%;
    border:solid 2px black;
    margin:5px;
`

function App() {
  const [count, setCount] = useState(0);

  const dispatch = useDispatch();

  const plusOrMinus = (e) => {
    if (e.target.innerText === '+') {
      setCount(count + 1);

      dispatch(plus);
    }
    else {
      setCount(count - 1);

      dispatch(minus);
    }
  }

  //----------------------------------------------------------------------------


  return (
    <BackDiv>
      <StartPropsBox>
        <PropsBox1 props={count} />
      </StartPropsBox>
      <ReduxBox1 />
      <StartReduxBox />
      <button onClick={plusOrMinus}>+</button>
      <button onClick={plusOrMinus}>-</button>
    </BackDiv>
  );
}

export default App;
