import styled from "styled-components";
import PropsBox from "./Components/Props/PropsBox1";
import ReduxBox from "./Components/Redux/ReduxBox";
import { useState } from "react";

const BackDiv = styled.div`
  display:flex;
  align-items:center;
  justify-content:center;
  height:100vh;
`

const StartPropsBox = styled.div`
    display: flex;
    align-items:center;
    justify-content:center;
    width:40%;
    height: 90%;
    border:solid 2px black;
    margin:5px;
`

const StartReduxBox = styled.div`
    display: flex;
    align-items:center;
    justify-content:center;
    width:40%;
    height: 90%;
    border:solid 2px black;
    margin:5px;
`

function App() {
  const [count, setCount] = useState(0);

  const plusOrMinus = (e) => {
    if (e.target.innerText === '+') {
      setCount(count + 1);
    }
    else {
      setCount(count - 1);
    }
  }

  return (
    <BackDiv>
      <StartPropsBox props={count} />
      <StartReduxBox />
      <button onClick={plusOrMinus}>+</button>
      <button onClick={plusOrMinus}>-</button>
    </BackDiv>
  );
}

export default App;
